// client/src/Signup.js

import React, { useState } from 'react';
import axios from 'axios';

function Signup() {
  // State hooks for form inputs and errors
  const [nickname, setNickname] = useState('');
  const [grade, setGrade] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [nicknameError, setNicknameError] = useState('');

  // Function to handle nickname change and check for duplicates
  const handleNicknameChange = async (e) => {
    const value = e.target.value;
    setNickname(value);

    if (value) {
      try {
        const response = await axios.post('/check_nickname', { nickname: value });
        if (response.data.exists) {
          setNicknameError('이미 존재하는 이름입니다');
        } else {
          setNicknameError('');
        }
      } catch (error) {
        console.error('Error checking nickname:', error);
      }
    } else {
      setNicknameError('');
    }
  };

  // Function to handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (password !== confirmPassword) {
      alert('Passwords do not match');
      return;
    }

    try {
      const response = await axios.post('/signup', {
        nickname,
        grade,
        password,
      });

      if (response.data.success) {
        alert('회원가입이 완료되었습니다.');
        setNickname('');
        setGrade('');
        setPassword('');
        setConfirmPassword('');
      } else {
        alert('회원가입에 실패했습니다.');
      }
    } catch (error) {
      console.error('Error during signup:', error);
    }
  };

  return (
    <div className='Signup'>
      <h2>회원가입</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>닉네임:</label>
          <input type="text" value={nickname} onChange={handleNicknameChange} />
          {nicknameError && <p style={{ color: 'red' }}>{nicknameError}</p>}
        </div>
        <div>
          <label>학년:</label>
          <input type="text" value={grade} onChange={(e) => setGrade(e.target.value)} />
        </div>
        <div>
          <label>비밀번호:</label>
          <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
        </div>
        <div>
          <label>비밀번호 재확인:</label>
          <input type="password" value={confirmPassword} onChange={(e) => setConfirmPassword(e.target.value)} />
        </div>
        <button type="submit">회원가입</button>
      </form>
    </div>
  );
}

export default Signup;
