import React, { useState, useEffect } from 'react';
import Signup from './Signup';

function App() {
  // state를 빈 배열로 초기화
  const [users, setUsers] = useState([]);

  useEffect(() => {
    fetch('/users')
      .then(response => response.json())
      .then(data => {
        // 받아온 데이터를 users 변수에 update
        setUsers(data.users || []);
      })
      .catch(err => console.log(err));
  }, []);

  return (
    <div className='App'>
      <h1>회원가입 및 사용자 목록</h1>
      <Signup />
      <div>
        <h2>사용자 목록</h2>
        {users.length === 0 ? (
          // 사용자가 없거나 로딩 중일 때 처리
          <p>없음</p>
        ) : (
          users.map((user, index) => <p key={index}>{user.nickname}</p>)
        )}
      </div>
    </div>
  );
}

export default App;
